mapper.select:
select o.oid, order_no as orderNo, provider_oid as providerOid, order_date as orderDate, state, description, o.create_user_id, o.create_time, u.uid, u.user_name, p.name as providerName, p.linkman as providerLinkman, p.linkman_telphone as providerTelphone, p.fax as providerFax from t_order o left join t_sys_user u on o.create_user_id=u.uid left join t_provider p on o.provider_oid=p.oid where 1=1
and o.order_no like CONCAT('%',#{orderNo},'%')
and o.state = #{state}
